import React, { useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Fade from 'react-reveal/Fade';

import { ThemeContext } from '../../contexts/ThemeContext';

import { AiOutlineFolder } from "react-icons/ai";

import './Achievement.css'

function AchievementCard({ id, title, details, date, field, image }) {

    const { theme } = useContext(ThemeContext);

    const useStyles = makeStyles((t) => ({
        achievementCard: {
            backgroundColor: theme.primary30,
            "&:hover": {
                backgroundColor: theme.primary50,
            },
        },
    }));

    const classes = useStyles();
    return (
        <Fade bottom>
            <a  target="_blank" href="https://www.semanticscholar.org/paper/Comparative-Analysis-of-Entity-Identification-and-Sharma-Mohania/22895086a31b76e85c87bf8015154e4cd0a1d6b5/figure/2" class="fill-div">
                <div key={id} className={`achievement-card ${classes.achievementCard}`}>
                    <div className="achievecard-content">
                        <div className="achievecard-details1">
                            <h2 style={{ color: theme.tertiary }}>{title}</h2>
                            <p style={{ color: theme.tertiary80 }}>{details}</p>
                        </div>
                        <div className="achievecard-details2" style={{ color: theme.primary }}>
                            <h5>{date}</h5>
                            <div className="achievecard-field">
                                <h5>{field}</h5>
                            </div>
                        </div>
                    </div>
                    <div className="achievecard-imgcontainer">
                        <img src={image} alt="" />
                    </div>
                </div>
            </a>
        </Fade>

    )
}

export default AchievementCard
